package com.osu.euklicenka

import android.app.Activity
import android.content.Intent
import android.os.Bundle
import android.view.View
import android.widget.ImageView
import android.widget.Toast
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.delay
import kotlinx.coroutines.launch

class SplashScreen : Activity() {

    val splash_activity = CoroutineScope(Dispatchers.Main)




    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_splash_screen)


        val imageView1:ImageView = findViewById(R.id.image1)

        val imageView2:ImageView = findViewById(R.id.image2)


        imageView1.setOnClickListener { v: View? ->
            Toast.makeText(
                applicationContext,
                "http://sitprorodinu.cz/assets/media/files/loga/logo_NEW.jpg",
                Toast.LENGTH_SHORT
            ).show()
        }

        imageView2.setOnClickListener { v: View? ->
            Toast.makeText(
                applicationContext,
                "data:image/png;base64,",
                Toast.LENGTH_SHORT
            ).show()

        }




        splash_activity.launch {
            delay(3000)

            var intent = Intent(this@SplashScreen, MainActivity::class.java )
            startActivity(intent)
            finish()

        }


    }
}


